
document.addEventListener('DOMContentLoaded',function(){document.body.style.overflow="hidden";});$(window).on('load',function(){$('#preloader').fadeOut(500);document.body.style.overflow="";});window.onload=function(){const productCard=document.querySelector('#product-card');const nav=document.querySelector('nav');const footer=document.querySelector('footer');const productCardContent=document.querySelector('#product-card-content');const footerHeight=footer.getBoundingClientRect().height;const navHeight=nav.getBoundingClientRect().height;const whiteBlock=document.querySelector('#white-block');$('#white-block').hide();whiteBlock.style.visibility='visible';$('#product-card').hide();productCard.style.visibility='visible';let scrollHeightTrue=Math.max(document.body.scrollHeight,document.documentElement.scrollHeight,document.body.offsetHeight,document.documentElement.offsetHeight,document.body.clientHeight,document.documentElement.clientHeight);let productCardHeight=document.documentElement.clientHeight-navHeight-footerHeight+'px';productCard.style.height=scrollHeightTrue+'px';let lastOffset=0;document.querySelector('.products-block').addEventListener('click',function(event){let el=event.target.closest('div');if(!el)return;lastOffset=window.pageYOffset;$('#white-block').fadeIn(300,()=>{$('body').css({position:'fixed',height:'100%',overflow:'hidden'});$('nav, footer').css({position:'absolute'});$('footer').css({backgroundColor:'#168514'});$('footer h3, footer a').css({color:'#fff'});$('#product-card').css({top:window.pageYOffset+navHeight+'px',height:productCardHeight});$('#product-card').show();$('nav').animate({top:window.pageYOffset+'px'},500);$('footer').animate({top:window.pageYOffset+document.documentElement.clientHeight-footerHeight+'px'},500,'linear',()=>{$('#white-block').fadeOut(900);});});});document.querySelector('#product-card-return').addEventListener('click',function(){$('#white-block').fadeIn(900,()=>{$('#product-card').hide();$('nav').animate({top:0},500);$('footer').animate({top:scrollHeightTrue-footerHeight},500,'linear',()=>{$('footer, nav').css({position:'static'});$('body').css({position:'static',height:'auto',overflow:'visible'});$('footer').css({backgroundColor:'#fff'});$('footer h3, footer a').css({color:'#000'});window.scrollTo(0,lastOffset);$('#white-block').fadeOut(600);});});});}
let scrollHeight=Math.max(document.body.scrollHeight,document.documentElement.scrollHeight,document.body.offsetHeight,document.documentElement.offsetHeight,document.body.clientHeight,document.documentElement.clientHeight);$('#shadow').css({height:scrollHeight+'px'});if(document.documentElement.clientWidth>550){document.addEventListener('DOMContentLoaded',function(){$('#modal-menu, #shadow, #modal-reviews').hide();$('#modal-menu, #modal-reviews, #shadow').css({visibility:'visible'});$('#modal-btn, #footer-modal-btn').click(function(){$('#modal-menu, #shadow').fadeIn(300);document.body.style.overflow="hidden";});$('#modal-menu-close').click(function(){$('#modal-menu, #shadow').fadeOut(300);document.body.style.overflow="";})
$('#makeReview').click(function(){$('#modal-reviews, #shadow').fadeIn(300);document.body.style.overflow="hidden";});$('#modal-reviews-close').click(function(){$('#modal-reviews, #shadow').fadeOut(300);document.body.style.overflow="";});});}